import React from 'react';
import './Sidebar.css';

function Sidebar({ addElement, isPreviewMode, setIsPreviewMode }) {
  const handleAddElement = (type) => {
    if (!isPreviewMode) {
      addElement(type);
    }
  };

  const togglePreviewMode = () => {
    setIsPreviewMode(!isPreviewMode);
  };

  return (
    <div className="sidebar">
      <div className="preview-toggle">
        <button 
          className={`preview-btn ${isPreviewMode ? 'active' : ''}`} 
          onClick={togglePreviewMode}
          title={isPreviewMode ? "Switch to Edit Mode" : "Preview"}
        >
          {isPreviewMode ? (
            <>
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04c.39-.39.39-1.02 0-1.41l-2.34-2.34c-.39-.39-1.02-.39-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z" fill="currentColor"/>
              </svg>
              <span>Edit</span>
            </>
          ) : (
            <>
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 4.5C7 4.5 2.73 7.61 1 12c1.73 4.39 6 7.5 11 7.5s9.27-3.11 11-7.5c-1.73-4.39-6-7.5-11-7.5zM12 17c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5zm0-8c-1.66 0-3 1.34-3 3s1.34 3 3 3 3-1.34 3-3-1.34-3-3-3z" fill="currentColor"/>
              </svg>
              <span>Preview</span>
            </>
          )}
        </button>
      </div>

      <div className="element-buttons">
        <button 
          className="element-btn" 
          onClick={() => handleAddElement('text')}
          disabled={isPreviewMode}
        >
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M2.5 4v3h5v12h3V7h5V4h-13zm19 5h-9v3h3v7h3v-7h3V9z" fill="currentColor"/>
          </svg>
          <span>Text</span>
        </button>

        <button 
          className="element-btn" 
          onClick={() => handleAddElement('image')}
          disabled={isPreviewMode}
        >
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M21 19V5c0-1.1-.9-2-2-2H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2zM8.5 13.5l2.5 3.01L14.5 12l4.5 6H5l3.5-4.5z" fill="currentColor"/>
          </svg>
          <span>Image</span>
        </button>

        <button 
          className="element-btn" 
          onClick={() => handleAddElement('video')}
          disabled={isPreviewMode}
        >
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z" fill="currentColor"/>
          </svg>
          <span>Video</span>
        </button>
      </div>
    </div>
  );
}

export default Sidebar;
