import React, { useCallback, useRef, useEffect, useState } from 'react';
import Element from './Element';
import './Canvas.css';

function Canvas({ elements, selectedElement, setSelectedElement, updateElement, deleteElement, isPreviewMode, addElement, getDefaultStyle }) {
  const canvasRef = useRef(null);
  const contentRef = useRef(null);
  const [canvasBounds, setCanvasBounds] = useState({ left: 0, right: 0, top: 0, bottom: 0 });
  const [rulers, setRulers] = useState({ horizontal: [], vertical: [] });

  // Calculate canvas boundaries and create ruler markings
  useEffect(() => {
    function updateCanvasBounds() {
      if (canvasRef.current && contentRef.current) {
        const canvasRect = canvasRef.current.getBoundingClientRect();
        const contentRect = contentRef.current.getBoundingClientRect();

        // Get editor panel width
        const propertiesPanel = document.querySelector('.element-properties');
        const editorWidth = propertiesPanel ? propertiesPanel.getBoundingClientRect().width : 300;

        // Set boundary constraints for elements
        setCanvasBounds({
          left: 30, // Account for ruler
          top: 30,  // Account for ruler
          right: window.innerWidth - editorWidth,
          bottom: Math.max(contentRect.height, window.innerHeight)
        });

        // Generate ruler markings
        generateRulers(
          Math.max(contentRect.width, window.innerWidth - editorWidth - 30), 
          Math.max(contentRect.height, window.innerHeight - 30)
        );
      }
    }

    function generateRulers(width, height) {
      // Create horizontal ruler markings every 50px with major marks every 100px
      const horizontal = [];
      for (let i = 0; i <= width; i += 50) {
        horizontal.push({
          position: i,
          isMajor: i % 100 === 0
        });
      }

      // Create vertical ruler markings
      const vertical = [];
      for (let i = 0; i <= height; i += 50) {
        vertical.push({
          position: i,
          isMajor: i % 100 === 0
        });
      }

      setRulers({ horizontal, vertical });
    }

    updateCanvasBounds();
    window.addEventListener('resize', updateCanvasBounds);

    return () => {
      window.removeEventListener('resize', updateCanvasBounds);
    };
  }, []);

  // Deselect elements when clicking on the canvas background
  const handleCanvasClick = (e) => {
    if (e.target === contentRef.current && !isPreviewMode) {
      setSelectedElement(null);
    }
  };

  // Enforce boundaries for elements
  const enforceElementBoundaries = (id, newPosition, elementWidth, elementHeight) => {
    return {
      x: Math.max(canvasBounds.left, Math.min(newPosition.x, canvasBounds.right - elementWidth - 30)), // 30px buffer
      y: Math.max(canvasBounds.top, Math.min(newPosition.y, canvasBounds.bottom - elementHeight))
    };
  };

  // Handle element dragging
  const handleElementDrag = (id, position, elementWidth, elementHeight) => {
    if (!isPreviewMode) {
      const constrainedPosition = enforceElementBoundaries(id, position, elementWidth, elementHeight);
      updateElement(id, { position: constrainedPosition });
    }
  };

  // Handle file drops
  const handleDrop = useCallback((e) => {
    e.preventDefault();
    if (isPreviewMode) return;

    const canvasRect = canvasRef.current.getBoundingClientRect();
    const position = {
      x: e.clientX - canvasRect.left,
      y: e.clientY - canvasRect.top
    };

    // Handle image file drops
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const file = e.dataTransfer.files[0];

      if (file.type.startsWith('image/')) {
        const reader = new FileReader();
        reader.onload = (event) => {
          addElement({
            id: `element-${Date.now()}`,
            type: 'image',
            content: event.target.result,
            style: getDefaultStyle('image'),
            position: position
          });
        };
        reader.readAsDataURL(file);
      }
    }
  }, [isPreviewMode, addElement, getDefaultStyle]);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
  }, []);

  return (
    <div className="canvas-container" ref={canvasRef}>
      <div 
        className={`canvas ${isPreviewMode ? 'preview-mode' : ''}`}
        onClick={handleCanvasClick}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        {!isPreviewMode && (
          <>
            <div className="ruler-corner"></div>

            <div className="horizontal-ruler">
              {rulers.horizontal.map((mark, index) => (
                <div 
                  key={`h-${index}`}
                  className={`ruler-mark ${mark.isMajor ? 'major' : 'minor'}`}
                  style={{ left: `${mark.position}px` }}
                >
                  {mark.isMajor && <span className="ruler-label">{mark.position}</span>}
                </div>
              ))}
            </div>

            <div className="vertical-ruler">
              {rulers.vertical.map((mark, index) => (
                <div 
                  key={`v-${index}`}
                  className={`ruler-mark ${mark.isMajor ? 'major' : 'minor'}`}
                  style={{ top: `${mark.position}px` }}
                >
                  {mark.isMajor && <span className="ruler-label">{mark.position}</span>}
                </div>
              ))}
            </div>
          </>
        )}

        <div className="canvas-content" ref={contentRef}>
          {elements.map(element => (
            <Element
              key={element.id}
              element={element}
              isSelected={element.id === selectedElement}
              onSelect={setSelectedElement}
              onUpdate={(id, updates, isNew = false) => {
                if (isNew) {
                  addElement(updates);
                } else {
                  updateElement(id, updates);
                }
              }}
              onDelete={deleteElement}
              isPreviewMode={isPreviewMode}
              canvasBounds={canvasBounds}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Canvas;
