import React, { useState } from 'react';
import './ElementProperties.css';

function ElementProperties({ element, updateElement }) {
  if (!element) return null;

  const handleContentChange = (e) => {
    updateElement(element.id, { content: e.target.value });
  };

  const handleStyleChange = (property, value) => {
    updateElement(element.id, { 
      style: { ...element.style, [property]: value } 
    });
  };

  const handleResetStyle = (property) => {
    const defaultValues = {
      fontSize: '16px',
      color: '#000000',
      fontWeight: 'normal',
      fontStyle: 'normal',
      textAlign: 'left',
      letterSpacing: '0px',
      lineHeight: '1.5'
    };

    handleStyleChange(property, defaultValues[property]);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        updateElement(element.id, { content: event.target.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleVideoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size <= 50000000) { // Limit to 50MB
        const videoUrl = URL.createObjectURL(file);
        updateElement(element.id, { content: videoUrl });
      } else {
        alert('Video file is too large. Maximum size is 50MB.');
      }
    }
  };

  return (
    <div className="element-properties">
      {element.type === 'text' && (
        <>
          <div className="property-header">
            <h3>TEXT EDITOR</h3>
            <button className="reset-btn" onClick={() => handleStyleChange('content', '')}>Clear</button>
          </div>

          <div className="property-group">
            <textarea 
              value={element.content} 
              onChange={handleContentChange}
              rows={6}
              placeholder="Enter your text here..."
            />
          </div>

          <div className="property-header">
            <h3>TEXT STYLE</h3>
            <button className="reset-btn" onClick={() => handleResetStyle('fontFamily')}>Reset</button>
          </div>

          <div className="property-group">
            <div className="font-dropdown-container">
              <select 
                className="font-select"
                value={element.style.fontFamily || 'Arial'} 
                onChange={(e) => handleStyleChange('fontFamily', e.target.value)}
              >
                {[
                  { name: 'Arimo', value: 'Arimo, sans-serif' },
                  { name: 'Canva Sans', value: 'Canva Sans, sans-serif' },
                  { name: 'Montserrat', value: 'Montserrat, sans-serif' },
                  { name: 'Open Sans', value: 'Open Sans, sans-serif' },
                  { name: 'Poppins', value: 'Poppins, sans-serif' },
                  { name: 'Glacial Indifference', value: 'Glacial Indifference, sans-serif' },
                  { name: 'League Spartan', value: 'League Spartan, sans-serif' },
                  { name: 'Anton', value: 'Anton, sans-serif' },
                  { name: 'DM Sans', value: 'DM Sans, sans-serif' },
                  { name: 'Archivo Black', value: 'Archivo Black, sans-serif' },
                  { name: 'Roboto', value: 'Roboto, sans-serif' },
                  { name: 'Garet', value: 'Garet, sans-serif' },
                  { name: 'Alice', value: 'Alice, serif' },
                  { name: 'Open Sauce', value: 'Open Sauce, sans-serif' },
                  { name: 'Brittany', value: 'Brittany, cursive' },
                  { name: 'Arial', value: 'Arial, sans-serif' }
                ].map(font => (
                  <option 
                    key={font.value} 
                    value={font.value} 
                    style={{ fontFamily: font.value, fontSize: '16px' }}
                  >
                    {font.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="property-header">
            <h3>TEXT SIZE</h3>
            <button className="reset-btn" onClick={() => handleResetStyle('fontSize')}>Reset</button>
          </div>

          <div className="property-group">
            <div className="slider-container">
              <input 
                type="range" 
                min="8" 
                max="72" 
                value={parseInt(element.style.fontSize) || 16} 
                onChange={(e) => handleStyleChange('fontSize', `${e.target.value}px`)}
                className="slider"
              />
              <div className="size-input-container">
                <input 
                  type="text" 
                  value={(element.style.fontSize || '16px').replace('px', '')} 
                  onChange={(e) => handleStyleChange('fontSize', `${e.target.value}px`)}
                  className="size-input"
                />
                <span className="size-unit">pt</span>
              </div>
            </div>
          </div>

          <div className="property-header">
            <h3>TEXT COLOR</h3>
            <button className="reset-btn" onClick={() => handleResetStyle('color')}>Reset</button>
          </div>

          <div className="property-group">
            <div className="color-picker">
              <div className="current-color">
                <div 
                  className="color-preview" 
                  style={{ backgroundColor: element.style.color || '#000000' }}
                ></div>
                <input 
                  type="text" 
                  value={element.style.color || '#000000'} 
                  onChange={(e) => handleStyleChange('color', e.target.value)}
                  className="color-input"
                />
                <input 
                  type="color" 
                  value={element.style.color || '#000000'} 
                  onChange={(e) => handleStyleChange('color', e.target.value)}
                  className="color-selector"
                />
              </div>
              <div className="color-presets">
                <div className="preset-row">
                  <button className="color-preset" style={{backgroundColor: '#f8d7da'}} onClick={() => handleStyleChange('color', '#f8d7da')}></button>
                  <button className="color-preset" style={{backgroundColor: '#fff3cd'}} onClick={() => handleStyleChange('color', '#fff3cd')}></button>
                  <button className="color-preset" style={{backgroundColor: '#ffffcc'}} onClick={() => handleStyleChange('color', '#ffffcc')}></button>
                  <button className="color-preset" style={{backgroundColor: '#d4edda'}} onClick={() => handleStyleChange('color', '#d4edda')}></button>
                  <button className="color-preset" style={{backgroundColor: '#d1ecf1'}} onClick={() => handleStyleChange('color', '#d1ecf1')}></button>
                  <button className="color-preset" style={{backgroundColor: '#e2d8f0'}} onClick={() => handleStyleChange('color', '#e2d8f0')}></button>
                  <button className="color-preset" style={{backgroundColor: '#f6d8e5'}} onClick={() => handleStyleChange('color', '#f6d8e5')}></button>
                </div>
                <div className="preset-row">
                  <button className="color-preset" style={{backgroundColor: '#e57373'}} onClick={() => handleStyleChange('color', '#e57373')}></button>
                  <button className="color-preset" style={{backgroundColor: '#ffd54f'}} onClick={() => handleStyleChange('color', '#ffd54f')}></button>
                  <button className="color-preset" style={{backgroundColor: '#fff176'}} onClick={() => handleStyleChange('color', '#fff176')}></button>
                  <button className="color-preset" style={{backgroundColor: '#81c784'}} onClick={() => handleStyleChange('color', '#81c784')}></button>
                  <button className="color-preset" style={{backgroundColor: '#4fc3f7'}} onClick={() => handleStyleChange('color', '#4fc3f7')}></button>
                  <button className="color-preset" style={{backgroundColor: '#9575cd'}} onClick={() => handleStyleChange('color', '#9575cd')}></button>
                  <button className="color-preset" style={{backgroundColor: '#f06292'}} onClick={() => handleStyleChange('color', '#f06292')}></button>
                </div>
              </div>
            </div>
          </div>

          <div className="property-header">
            <h3>TEXT FORMAT</h3>
            <button className="reset-btn" onClick={() => {
              handleResetStyle('fontWeight');
              handleResetStyle('fontStyle');
              handleResetStyle('textAlign');
            }}>Reset</button>
          </div>

          <div className="property-group">
            <div className="format-options">
              <div className="case-options">
                <button 
                  className={`format-btn ${element.style.textTransform === 'uppercase' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('textTransform', 'uppercase')}
                >
                  AA
                </button>
                <button 
                  className={`format-btn ${element.style.textTransform === 'lowercase' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('textTransform', 'lowercase')}
                >
                  aa
                </button>
                <button 
                  className={`format-btn ${element.style.textTransform === 'capitalize' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('textTransform', 'capitalize')}
                >
                  Aa
                </button>
              </div>
              <div className="style-options">
                <button 
                  className={`format-btn ${element.style.fontWeight === 'bold' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('fontWeight', element.style.fontWeight === 'bold' ? 'normal' : 'bold')}
                >
                  B
                </button>
                <button 
                  className={`format-btn ${element.style.fontStyle === 'italic' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('fontStyle', element.style.fontStyle === 'italic' ? 'normal' : 'italic')}
                >
                  I
                </button>
                <button 
                  className={`format-btn ${element.style.textDecoration === 'underline' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('textDecoration', element.style.textDecoration === 'underline' ? 'none' : 'underline')}
                >
                  U
                </button>
              </div>
            </div>
            <div className="align-options">
              <button 
                className={`align-btn ${element.style.textAlign === 'left' ? 'active' : ''}`}
                onClick={() => handleStyleChange('textAlign', 'left')}
              >
                <span className="align-icon">&#8592;</span>
              </button>
              <button 
                className={`align-btn ${element.style.textAlign === 'center' ? 'active' : ''}`}
                onClick={() => handleStyleChange('textAlign', 'center')}
              >
                <span className="align-icon">&#8596;</span>
              </button>
              <button 
                className={`align-btn ${element.style.textAlign === 'right' ? 'active' : ''}`}
                onClick={() => handleStyleChange('textAlign', 'right')}
              >
                <span className="align-icon">&#8594;</span>
              </button>
            </div>
          </div>

          <div className="property-header">
            <h3>SPACING</h3>
            <button className="reset-btn" onClick={() => {
              handleResetStyle('letterSpacing');
              handleResetStyle('lineHeight');
            }}>Reset</button>
          </div>

          <div className="property-group">
            <div className="spacing-control">
              <label>LETTER SPACING</label>
              <div className="slider-container">
                <input 
                  type="range" 
                  min="-2" 
                  max="10" 
                  step="0.1"
                  value={parseFloat(element.style.letterSpacing) || 0} 
                  onChange={(e) => handleStyleChange('letterSpacing', `${e.target.value}px`)}
                  className="slider"
                />
                <div className="size-input-container">
                  <input 
                    type="text" 
                    value={`${parseInt((parseFloat(element.style.letterSpacing) || 0) * 100)}%`} 
                    readOnly
                    className="size-input"
                  />
                </div>
              </div>
            </div>

            <div className="spacing-control">
              <label>LINE HEIGHT</label>
              <div className="slider-container">
                <input 
                  type="range" 
                  min="0.5" 
                  max="3" 
                  step="0.01"
                  value={parseFloat(element.style.lineHeight) || 1.5} 
                  onChange={(e) => handleStyleChange('lineHeight', e.target.value)}
                  className="slider"
                />
                <div className="size-input-container">
                  <input 
                    type="text" 
                    value={parseFloat(element.style.lineHeight) || 1.5} 
                    readOnly
                    className="size-input"
                  />
                </div>
              </div>
            </div>
          </div>
        </>
      )}

      {element.type === 'image' && (
        <>
          <div className="property-header">
            <h3>EDIT IMAGE</h3>
            <button className="reset-btn" onClick={() => updateElement(element.id, { content: '' })}>Clear</button>
          </div>

          <div className="property-group">
            <div className="upload-container">
              <label htmlFor="image-upload" className="upload-btn">Upload Image</label>
              <input 
                id="image-upload" 
                type="file" 
                accept="image/*" 
                onChange={handleImageUpload}
                style={{ display: 'none' }} 
              />
              <p className="or-divider">OR</p>
              <input 
                type="text" 
                value={element.content} 
                onChange={handleContentChange}
                placeholder="Image URL"
                className="full-width-input"
              />
            </div>

            {element.content && (
              <div className="image-preview">
                <img src={element.content} alt="Preview" />
              </div>
            )}
          </div>
        </>
      )}

      {element.type === 'video' && (
        <>
          <div className="property-header">
            <h3>EDIT VIDEO</h3>
            <button className="reset-btn" onClick={() => updateElement(element.id, { content: '' })}>Clear</button>
          </div>

          <div className="property-group">
            <div className="upload-container">
              <label htmlFor="video-upload" className="upload-btn">Upload Video</label>
              <input 
                id="video-upload" 
                type="file" 
                accept="video/*" 
                onChange={handleVideoUpload}
                style={{ display: 'none' }} 
              />
              <p className="or-divider">OR</p>
              <input 
                type="text" 
                value={element.content} 
                onChange={handleContentChange}
                placeholder="Video URL (YouTube or direct link)"
                className="full-width-input"
              />
            </div>

            {element.content && !element.content.includes('youtube') && !element.content.includes('youtu.be') && (
              <div className="video-preview">
                <video src={element.content} controls style={{ maxWidth: '100%', maxHeight: '150px' }}></video>
              </div>
            )}

            {element.content && (element.content.includes('youtube') || element.content.includes('youtu.be')) && (
              <div className="video-preview">
                <div>YouTube video will appear on canvas</div>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default ElementProperties;
