import React, { useCallback, useEffect } from 'react';
import Element from './Element';
import './Canvas.css';

function Canvas({ elements, selectedElement, setSelectedElement, updateElement, deleteElement, isPreviewMode, addElement, getDefaultStyle }) {
  const handleCanvasClick = (e) => {
    // Deselect when clicking on the canvas background
    if (e.target === e.currentTarget && !isPreviewMode) {
      setSelectedElement(null);
    }
  };

  const handleElementDrag = (id, position) => {
    if (!isPreviewMode) {
      updateElement(id, { position });
    }
  };

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    if (isPreviewMode) return;

    // Get drop position
    const rect = e.currentTarget.getBoundingClientRect();
    const position = {
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    };

    // Check if files were dropped
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const file = e.dataTransfer.files[0];

      // Check if it's an image
      if (file.type.startsWith('image/')) {
        const reader = new FileReader();
        reader.onload = (event) => {
          // Create a new image element
          const newElement = {
            id: `element-${Date.now()}`,
            type: 'image',
            content: event.target.result,
            style: getDefaultStyle('image'),
            position: position
          };
          addElement(newElement);
        };
        reader.readAsDataURL(file);
      }
    }
  }, [isPreviewMode, addElement, getDefaultStyle]);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
  }, []);

  // Add keyboard event listener for Delete key
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Only handle delete key if we're not in preview mode and an element is selected
      if (e.key === 'Delete' && selectedElement && !isPreviewMode) {
        // Prevent default browser behavior
        e.preventDefault();

        // Delete the selected element
        deleteElement(selectedElement);

        // Clear selection
        setSelectedElement(null);
      }
    };

    // Add the event listener
    document.addEventListener('keydown', handleKeyDown);

    // Clean up the event listener when component unmounts
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [selectedElement, deleteElement, isPreviewMode, setSelectedElement]);

  return (
    <div 
      className={`canvas ${isPreviewMode ? 'preview-mode' : ''}`} 
      onClick={handleCanvasClick}
      onDrop={handleDrop}
      onDragOver={handleDragOver}
    >
      {elements.map(element => (
        <Element
          key={element.id}
          element={element}
          isSelected={element.id === selectedElement}
          onSelect={setSelectedElement}
          onUpdate={(id, updates, isNew = false) => {
            if (isNew) {
              // This is for adding a duplicated element
              addElement(updates);
            } else {
              // Normal update
              updateElement(id, updates);
            }
          }}
          onDelete={deleteElement}
          isPreviewMode={isPreviewMode}
        />
      ))}
    </div>
  );
}

export default Canvas;
