import React, { useState, useRef, useEffect } from 'react';
import './ElementProperties.css';

function ElementProperties({ element, updateElement }) {
  const [isFontDropdownOpen, setIsFontDropdownOpen] = useState(false);
  const fontDropdownRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (fontDropdownRef.current && !fontDropdownRef.current.contains(event.target)) {
        setIsFontDropdownOpen(false);
      }
    }

    if (isFontDropdownOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isFontDropdownOpen]);

  const fonts = [
    { name: 'Arimo', value: 'Arimo, sans-serif' },
    { name: 'Canva Sans', value: 'Canva Sans, sans-serif' },
    { name: 'Montserrat', value: 'Montserrat, sans-serif' },
    { name: 'Open Sans', value: 'Open Sans, sans-serif' },
    { name: 'Poppins', value: 'Poppins, sans-serif' },
    { name: 'Glacial Indifference', value: 'Glacial Indifference, sans-serif' },
    { name: 'League Spartan', value: 'League Spartan, sans-serif' },
    { name: 'Anton', value: 'Anton, sans-serif' },
    { name: 'DM Sans', value: 'DM Sans, sans-serif' },
    { name: 'Archivo Black', value: 'Archivo Black, sans-serif' },
    { name: 'Roboto', value: 'Roboto, sans-serif' },
    { name: 'Garet', value: 'Garet, sans-serif' },
    { name: 'Alice', value: 'Alice, serif' },
    { name: 'Open Sauce', value: 'Open Sauce, sans-serif' },
    { name: 'Brittany', value: 'Brittany, cursive' },
    { name: 'Arial', value: 'Arial, sans-serif' }
  ];

  // Return early if no element is selected
  if (!element) return null;

  const handleContentChange = (e) => {
    updateElement(element.id, { content: e.target.value });
  };

  const handleStyleChange = (property, value) => {
    updateElement(element.id, {
      style: {
        ...element.style,
        [property]: value
      }
    });
  };

  const handleResetStyle = (property) => {
    const defaultValues = {
      fontSize: '16px',
      color: '#000000',
      fontWeight: 'normal',
      fontStyle: 'normal',
      textAlign: 'left',
      letterSpacing: '0px',
      lineHeight: '1.5'
    };

    handleStyleChange(property, defaultValues[property]);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        updateElement(element.id, { content: event.target.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleVideoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size <= 50000000) { // Limit to 50MB
        const videoUrl = URL.createObjectURL(file);
        updateElement(element.id, { content: videoUrl });
      } else {
        alert('Video file is too large. Maximum size is 50MB.');
      }
    }
  };

  // Render content based on element type
  const renderProperties = () => {
    switch(element.type) {
      case 'text':
        return (
          <div className="property-section">
            <h4>Text Properties</h4>

            {/* Text content */}
            <div className="property-row">
              <label>Content</label>
              <textarea
                value={element.content}
                onChange={handleContentChange}
                rows={3}
              />
            </div>

            {/* Font Family */}
            <div className="property-row">
              <label>Font</label>
              <div className="font-dropdown-container" ref={fontDropdownRef}>
                <div 
                  className="font-dropdown-header" 
                  onClick={() => setIsFontDropdownOpen(!isFontDropdownOpen)}
                >
                  <span style={{ fontFamily: element.style.fontFamily || 'Arial, sans-serif' }}>
                    {(element.style.fontFamily || 'Arial, sans-serif').split(',')[0].replace(/['"]/g, '')}
                  </span>
                  <span className="dropdown-arrow"></span>
                </div>

                {isFontDropdownOpen && (
                  <div className="font-dropdown-list">
                    {fonts.map(font => (
                      <div 
                        key={font.value}
                        className={`font-dropdown-item ${element.style.fontFamily === font.value ? 'selected' : ''}`}
                        onClick={() => {
                          handleStyleChange('fontFamily', font.value);
                          setIsFontDropdownOpen(false);
                        }}
                      >
                        <span 
                          className="font-name" 
                          style={{ fontFamily: font.value }}
                        >
                          {font.name}
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Font Size */}
            <div className="property-row property-slider">
              <label>SIZE</label>
              <div className="modern-slider-container">
                <input 
                  type="range" 
                  min="8" 
                  max="72" 
                  value={parseInt(element.style.fontSize) || 16} 
                  onChange={(e) => {
                    const value = e.target.value;
                    e.target.style.setProperty('--value-percent', `${(value - 8) / 64 * 100}%`);
                    handleStyleChange('fontSize', `${value}px`);
                  }}
                  className="modern-slider"
                  style={{ '--value-percent': `${((parseInt(element.style.fontSize) || 16) - 8) / 64 * 100}%` }}
                />
                <div className="slider-value">{parseInt(element.style.fontSize) || 16}px</div>
              </div>
            </div>

            {/* Text Color */}
            <div className="property-row">
              <label>Color</label>
              <input 
                type="color" 
                value={element.style.color || '#000000'} 
                onChange={(e) => handleStyleChange('color', e.target.value)}
              />
            </div>

            {/* Text Alignment */}
            <div className="property-row">
              <label>Alignment</label>
              <div className="button-group">
                <button 
                  className={`align-btn ${element.style.textAlign === 'left' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('textAlign', 'left')}
                  title="Align Left"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M3 7H21V9H3V7ZM3 11H15V13H3V11ZM3 15H21V17H3V15ZM3 19H15V21H3V19Z" fill="currentColor"/>
                  </svg>
                </button>
                <button 
                  className={`align-btn ${element.style.textAlign === 'center' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('textAlign', 'center')}
                  title="Align Center"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M3 7H21V9H3V7ZM5 11H19V13H5V11ZM3 15H21V17H3V15ZM5 19H19V21H5V19Z" fill="currentColor"/>
                  </svg>
                </button>
                <button 
                  className={`align-btn ${element.style.textAlign === 'right' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('textAlign', 'right')}
                  title="Align Right"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M3 7H21V9H3V7ZM9 11H21V13H9V11ZM3 15H21V17H3V15ZM9 19H21V21H9V19Z" fill="currentColor"/>
                  </svg>
                </button>
              </div>
            </div>

            {/* Text Styling */}
            <div className="property-row">
              <label>Style</label>
              <div className="button-group">
                <button 
                  className={`style-btn ${element.style.fontWeight === 'bold' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('fontWeight', element.style.fontWeight === 'bold' ? 'normal' : 'bold')}
                  title="Bold"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M15.6 11.79C16.57 11.12 17.25 10.02 17.25 9C17.25 6.74 15.5 5 13.25 5H7V19H14.04C16.13 19 17.75 17.39 17.75 15.3C17.75 13.67 16.89 12.29 15.6 11.79ZM10 7.5H13C13.83 7.5 14.5 8.17 14.5 9C14.5 9.83 13.83 10.5 13 10.5H10V7.5ZM13.5 16.5H10V13.5H13.5C14.33 13.5 15 14.17 15 15C15 15.83 14.33 16.5 13.5 16.5Z" fill="currentColor"/>
                  </svg>
                </button>
                <button 
                  className={`style-btn ${element.style.fontStyle === 'italic' ? 'active' : ''}`}
                  onClick={() => handleStyleChange('fontStyle', element.style.fontStyle === 'italic' ? 'normal' : 'italic')}
                  title="Italic"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M10 5V8H12.21L8.79 16H6V19H14V16H11.79L15.21 8H18V5H10Z" fill="currentColor"/>
                  </svg>
                </button>
              </div>
            </div>

            {/* Line Height */}
            <div className="property-row property-slider">
              <label>LINE HEIGHT</label>
              <div className="modern-slider-container">
                <input 
                  type="range" 
                  min="10" 
                  max="30" 
                  value={parseFloat(element.style.lineHeight || 1.5) * 10} 
                  onChange={(e) => {
                    const value = e.target.value / 10;
                    e.target.style.setProperty('--value-percent', `${(value - 1) / 2 * 100}%`);
                    handleStyleChange('lineHeight', value.toString());
                  }}
                  className="modern-slider"
                  style={{ '--value-percent': `${(parseFloat(element.style.lineHeight || 1.5) - 1) / 2 * 100}%` }}
                />
                <div className="slider-value">{parseFloat(element.style.lineHeight || 1.5).toFixed(1)}</div>
              </div>
            </div>

            {/* Letter Spacing */}
            <div className="property-row property-slider">
              <label>LETTER SPACING</label>
              <div className="modern-slider-container">
                <input 
                  type="range" 
                  min="0" 
                  max="10" 
                  value={parseInt(element.style.letterSpacing) || 0} 
                  onChange={(e) => {
                    const value = e.target.value;
                    e.target.style.setProperty('--value-percent', `${value * 10}%`);
                    handleStyleChange('letterSpacing', `${value}px`);
                  }}
                  className="modern-slider"
                  style={{ '--value-percent': `${(parseInt(element.style.letterSpacing) || 0) * 10}%` }}
                />
                <div className="slider-value">{parseInt(element.style.letterSpacing) || 0}px</div>
              </div>
            </div>
          </div>
        );

      case 'image':
        return (
          <div className="property-section">
            <h4>Image Properties</h4>

            {/* Image Preview */}
            <div className="image-preview">
              {element.content ? (
                <img 
                  src={element.content} 
                  alt="Preview" 
                  style={{ maxWidth: '100%', maxHeight: '150px' }} 
                />
              ) : (
                <div className="empty-preview">No image selected</div>
              )}
            </div>

            {/* Image Upload */}
            <div className="upload-container">
              <label className="upload-btn">
                Upload Image
                <input 
                  type="file" 
                  accept="image/*" 
                  onChange={handleImageUpload} 
                  style={{ display: 'none' }} 
                />
              </label>

              <div className="or-divider">OR</div>

              <div className="property-row">
                <input 
                  type="text" 
                  className="full-width-input"
                  placeholder="Enter image URL..." 
                  value={element.content || ''}
                  onChange={(e) => updateElement(element.id, { content: e.target.value })}
                />
              </div>
            </div>

            {/* Object Fit */}
            <div className="property-row">
              <label>Object Fit</label>
              <select 
                value={element.style.objectFit || 'contain'}
                onChange={(e) => handleStyleChange('objectFit', e.target.value)}
                className="full-width-input"
              >
                <option value="contain">Contain</option>
                <option value="cover">Cover</option>
                <option value="fill">Fill</option>
                <option value="none">None</option>
              </select>
            </div>

            {/* Opacity */}
            <div className="property-row property-slider">
              <label>OPACITY</label>
              <div className="modern-slider-container">
                <input 
                  type="range" 
                  min="0" 
                  max="100" 
                  value={Math.round((element.style.opacity || 1) * 100)} 
                  onChange={(e) => {
                    const value = e.target.value;
                    e.target.style.setProperty('--value-percent', `${value}%`);
                    handleStyleChange('opacity', value / 100);
                  }}
                  className="modern-slider"
                  style={{ '--value-percent': `${Math.round((element.style.opacity || 1) * 100)}%` }}
                />
                <div className="slider-value">{Math.round((element.style.opacity || 1) * 100)}%</div>
              </div>
            </div>
          </div>
        );

      case 'video':
        return (
          <div className="property-section">
            <h4>Video Properties</h4>

            {/* Video Source URL */}
            <div className="property-row">
              <label>Video URL</label>
              <input 
                type="text" 
                className="full-width-input"
                placeholder="YouTube URL or direct video link" 
                value={element.content || ''}
                onChange={(e) => updateElement(element.id, { content: e.target.value })}
              />
            </div>

            {/* Video Upload */}
            <div className="property-row">
              <label>Upload Video</label>
              <label className="upload-btn">
                Choose Video File
                <input 
                  type="file" 
                  accept="video/*" 
                  onChange={handleVideoUpload} 
                  style={{ display: 'none' }} 
                />
              </label>
              <div className="file-size-note">Max file size: 50MB</div>
            </div>
          </div>
        );

      case 'shape':
        const shapeType = element.style.shapeType || 'square';
        return (
          <div className="property-section">
            <h4>Shape Properties</h4>

            {/* Shape type selector */}
            <div className="property-row">
              <label>Shape Type</label>
              <select
                value={shapeType}
                onChange={(e) => handleStyleChange('shapeType', e.target.value)}
                className="shape-selector"
              >
                <option value="square">Square</option>
                <option value="circle">Circle</option>
                <option value="triangle">Triangle</option>
                <option value="arrow">Arrow</option>
                <option value="line">Line</option>
                <option value="star">Star</option>
              </select>
            </div>

            {/* Color picker */}
            <div className="property-row">
              <label>Color</label>
              <input 
                type="color" 
                value={element.style.backgroundColor || '#3f51b5'} 
                onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
              />
            </div>

            {/* Opacity slider */}
            <div className="property-row property-slider">
              <label>OPACITY</label>
              <div className="modern-slider-container">
                <input 
                  type="range" 
                  min="0" 
                  max="100" 
                  value={Math.round((element.style.opacity || 1) * 100)} 
                  onChange={(e) => {
                    const value = e.target.value;
                    e.target.style.setProperty('--value-percent', `${value}%`);
                    handleStyleChange('opacity', value / 100);
                  }}
                  className="modern-slider"
                  style={{ '--value-percent': `${Math.round((element.style.opacity || 1) * 100)}%` }}
                />
                <div className="slider-value">{Math.round((element.style.opacity || 1) * 100)}%</div>
              </div>
            </div>

            {/* Border radius (for square only) */}
            {shapeType === 'square' && (
              <div className="property-row property-slider">
                <label>BORDER RADIUS</label>
                <div className="modern-slider-container">
                  <input 
                    type="range" 
                    min="0" 
                    max="50" 
                    value={parseInt(element.style.borderRadius) || 0} 
                    onChange={(e) => {
                      const value = e.target.value;
                      e.target.style.setProperty('--value-percent', `${value * 2}%`);
                      handleStyleChange('borderRadius', `${value}px`);
                    }}
                    className="modern-slider"
                    style={{ '--value-percent': `${(parseInt(element.style.borderRadius) || 0) * 2}%` }}
                  />
                  <div className="slider-value">{parseInt(element.style.borderRadius) || 0}px</div>
                </div>
              </div>
            )}

            {/* Shadow controls */}
            <div className="property-row">
              <label>Shadow</label>
              <div className="shadow-controls">
                <div className="shadow-toggle">
                  <input 
                    type="checkbox" 
                    id="shadow-toggle"
                    checked={element.style.boxShadow !== 'none' && element.style.boxShadow !== undefined}
                    onChange={(e) => {
                      handleStyleChange(
                        'boxShadow', 
                        e.target.checked ? '0px 4px 8px rgba(0,0,0,0.2)' : 'none'
                      );
                    }}
                  />
                  <label htmlFor="shadow-toggle">Enable Shadow</label>
                </div>
                {element.style.boxShadow !== 'none' && element.style.boxShadow !== undefined && (
                  <div className="shadow-sliders">
                    <div className="property-row property-slider">
                      <label>Blur</label>
                      <input 
                        type="range" 
                        min="0" 
                        max="20" 
                        value={getShadowBlur(element.style.boxShadow)} 
                        onChange={(e) => updateShadow('blur', e.target.value)}
                        className="modern-slider"
                      />
                      <div className="slider-value">{getShadowBlur(element.style.boxShadow)}px</div>
                    </div>
                    <div className="property-row property-slider">
                      <label>Spread</label>
                      <input 
                        type="range" 
                        min="0" 
                        max="20" 
                        value={getShadowSpread(element.style.boxShadow)} 
                        onChange={(e) => updateShadow('spread', e.target.value)}
                        className="modern-slider"
                      />
                      <div className="slider-value">{getShadowSpread(element.style.boxShadow)}px</div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        );

      default:
        return <div>Select an element to edit its properties</div>;
    }
  };

  // Helper functions for shadow manipulation
  const getShadowBlur = (shadow) => {
    if (!shadow || shadow === 'none') return 8;
    // Parse the shadow string to extract blur value
    const match = shadow.match(/0px 4px (\d+)px/);
    return match ? parseInt(match[1]) : 8;
  };

  const getShadowSpread = (shadow) => {
    if (!shadow || shadow === 'none') return 0;
    // Parse the shadow string to extract spread value
    const match = shadow.match(/0px 4px \d+px (\d+)px/);
    return match ? parseInt(match[1]) : 0;
  };

  const updateShadow = (property, value) => {
    const currentShadow = element.style.boxShadow || '0px 4px 8px rgba(0,0,0,0.2)';
    let shadowParts = currentShadow.match(/0px 4px (\d+)px( \d+px)? rgba\(0,0,0,(0\.\d+)\)/);

    if (!shadowParts) {
      // Default shadow if parsing fails
      shadowParts = [null, '8', '0', '0.2'];
    }

    let blur = parseInt(shadowParts[1]);
    let spread = shadowParts[2] ? parseInt(shadowParts[2].trim()) : 0;
    let opacity = parseFloat(shadowParts[3]);

    if (property === 'blur') blur = parseInt(value);
    if (property === 'spread') spread = parseInt(value);

    const newShadow = `0px 4px ${blur}px ${spread > 0 ? spread + 'px ' : ''}rgba(0,0,0,${opacity})`;
    handleStyleChange('boxShadow', newShadow);
  };

  return (
    <div className="element-properties">
      <div className="property-header">
        <h3>Element Properties</h3>
      </div>

      {renderProperties()}
    </div>
  );
}

export default ElementProperties;
