import React from 'react';
import Element from './Element';
import './Canvas.css';

function Canvas({ elements, selectedElement, setSelectedElement, updateElement, deleteElement }) {
  const handleCanvasClick = (e) => {
    // Deselect when clicking on the canvas background
    if (e.target === e.currentTarget) {
      setSelectedElement(null);
    }
  };

  const handleElementDrag = (id, position) => {
    updateElement(id, { position });
  };

  return (
    <div className="canvas" onClick={handleCanvasClick}>
      {elements.map(element => (
        <Element
          key={element.id}
          element={element}
          isSelected={selectedElement === element.id}
          onSelect={() => setSelectedElement(element.id)}
          onDrag={handleElementDrag}
          onDelete={deleteElement}
        />
      ))}
    </div>
  );
}

export default Canvas;
