import React from 'react';
import './Sidebar.css';

function Sidebar({ addElement, selectedElement, element, updateElement }) {
  const sidebarItems = [
    { id: 'text', icon: 'T', label: 'Text', action: () => addElement('text') },
    { id: 'brand', icon: 'ðŸ‘‘', label: 'Brand', action: () => console.log('Brand clicked') },
    { id: 'uploads', icon: 'â˜ï¸', label: 'Uploads', action: () => document.getElementById('file-upload').click() }
  ];

  // Get the currently active panel (if any)
  const getActivePanel = () => {
    if (!selectedElement) return null;
    if (element && element.type === 'text') return 'textPanel';
    if (element && element.type === 'image') return 'imagePanel';
    return null;
  };

  const activePanel = getActivePanel();

  return (
    <div className="sidebar-container">
      <div className="sidebar">
        {sidebarItems.map(item => (
          <div 
            key={item.id}
            className={`sidebar-item ${activePanel === item.id + 'Panel' ? 'active' : ''}`}
            onClick={item.action}
          >
            <div className="sidebar-icon">
              {item.icon}
            </div>
            <div className="sidebar-label">{item.label}</div>
          </div>
        ))}

        {/* Hidden file input for uploads */}
        <input
          id="file-upload"
          type="file"
          accept="image/*"
          style={{ display: 'none' }}
          onChange={(e) => {
            const file = e.target.files[0];
            if (file) {
              const reader = new FileReader();
              reader.onload = (event) => {
                addElement('image', event.target.result);
              };
              reader.readAsDataURL(file);
            }
          }}
        />
      </div>

      {/* This is where ElementProperties would be rendered when an element is selected */}
    </div>
  );
}

export default Sidebar;
