import React, { useState, useRef, useEffect } from 'react';
import './Element.css';

function Element({ 
  element, 
  isSelected, 
  onSelect, 
  onUpdate, 
  onDelete,
  isPreviewMode
}) {
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [isRotating, setIsRotating] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [startSize, setStartSize] = useState({ width: 0, height: 0 });
  const [startRotation, setStartRotation] = useState(0);
  const [currentRotation, setCurrentRotation] = useState(element.rotate || 0);

  const elementRef = useRef(null);

  useEffect(() => {
    setCurrentRotation(element.rotate || 0);
  }, [element.rotate]);

  const handleMouseDown = (e) => {
    if (isPreviewMode) return;

    e.stopPropagation();
    onSelect(element.id);

    if (e.target.classList.contains('resize-handle')) {
      handleResizeStart(e);
    } else if (e.target.classList.contains('rotation-handle') || e.target.closest('.rotation-handle')) {
      handleRotateStart(e);
    } else if (!e.target.closest('.element-controls') && !e.target.classList.contains('control-btn') && 
               e.target.tagName !== 'BUTTON' && !e.target.closest('button')) {
      if (element.type !== 'text' || !e.target.isContentEditable || e.target !== e.currentTarget.firstChild) {
        handleDragStart(e);
      }
    }
  };

  const handleDragStart = (e) => {
    e.preventDefault();
    setIsDragging(true);

    setStartPos({
      x: e.clientX - element.position.x,
      y: e.clientY - element.position.y
    });

    document.addEventListener('mousemove', handleDragMove);
    document.addEventListener('mouseup', handleDragEnd);
  };

  const handleDragMove = (e) => {
    if (!isDragging) return;

    const newX = e.clientX - startPos.x;
    const newY = e.clientY - startPos.y;

    onUpdate(element.id, {
      position: { x: newX, y: newY }
    });
  };

  const handleDragEnd = () => {
    setIsDragging(false);
    document.removeEventListener('mousemove', handleDragMove);
    document.removeEventListener('mouseup', handleDragEnd);
  };

  const handleResizeStart = (e) => {
    e.stopPropagation();
    e.preventDefault();
    setIsResizing(true);

    const elRect = elementRef.current.getBoundingClientRect();
    setStartPos({
      x: e.clientX,
      y: e.clientY
    });

    // Store the current width and height from the style or the actual element
    const width = parseInt(element.style.width) || elRect.width;
    const height = parseInt(element.style.height) || elRect.height;

    setStartSize({
      width: width,
      height: height
    });

    document.addEventListener('mousemove', handleResizeMove);
    document.addEventListener('mouseup', handleResizeEnd);
  };

  const handleResizeMove = (e) => {
    if (!isResizing) return;

    // Calculate the change in mouse position
    const dx = e.clientX - startPos.x;
    const dy = e.clientY - startPos.y;

    // Apply the change to get new dimensions
    const newWidth = Math.max(startSize.width + dx, 50); // Minimum width of 50px
    const newHeight = Math.max(startSize.height + dy, 50); // Minimum height of 50px

    // Update the element with the new dimensions
    onUpdate(element.id, {
      style: {
        ...element.style,
        width: `${newWidth}px`,
        height: `${newHeight}px`
      }
    });
  };

  const handleResizeEnd = () => {
    setIsResizing(false);
    document.removeEventListener('mousemove', handleResizeMove);
    document.removeEventListener('mouseup', handleResizeEnd);
  };

  const handleRotateStart = (e) => {
    e.stopPropagation();
    setIsRotating(true);

    const rect = elementRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    const startAngle = Math.atan2(
      e.clientY - centerY,
      e.clientX - centerX
    ) * (180 / Math.PI);

    setStartRotation(startAngle - (element.rotate || 0));

    document.addEventListener('mousemove', handleRotateMove);
    document.addEventListener('mouseup', handleRotateEnd);
  };

  const handleRotateMove = (e) => {
    if (!isRotating) return;

    const rect = elementRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    const currentAngle = Math.atan2(
      e.clientY - centerY,
      e.clientX - centerX
    ) * (180 / Math.PI);

    const newRotation = currentAngle - startRotation;
    setCurrentRotation(newRotation);

    onUpdate(element.id, { rotate: newRotation });
  };

  const handleRotateEnd = () => {
    setIsRotating(false);
    document.removeEventListener('mousemove', handleRotateMove);
    document.removeEventListener('mouseup', handleRotateEnd);
  };

  const handleDelete = (e) => {
    e.stopPropagation();
    onDelete(element.id);
  };

  const handleCopyElement = (e) => {
    e.stopPropagation();
    const newElement = {
      ...element,
      id: `element-${Date.now()}`,
      position: {
        x: element.position.x + 20,
        y: element.position.y + 20
      }
    };

    if (typeof onUpdate === 'function') {
      onUpdate(element.id, newElement, true);
    } else {
      console.error("onUpdate is not available for duplicating elements");
    }
  };

  const getElementContent = () => {
    switch(element.type) {
      case 'text':
        return (
          <div 
            style={{
              ...element.style,
              width: '100%',
              height: '100%',
              outline: 'none',
              boxSizing: 'border-box',
            }}
            contentEditable={!isPreviewMode}
            suppressContentEditableWarning={true}
            onMouseDown={(e) => {
              if (e.target === e.currentTarget && document.activeElement !== e.currentTarget) {
                e.stopPropagation();
              }
            }}
            onBlur={(e) => {
              onUpdate(element.id, { content: e.target.innerText });
            }}
          >
            {element.content}
          </div>
        );
      case 'image':
        return (
          <img 
            src={element.content} 
            alt="User uploaded content"
            style={{
              ...element.style,
              width: '100%', 
              height: '100%',
              objectFit: element.style.objectFit || 'contain',
              borderRadius: element.style.borderRadius || '0px',
              opacity: element.style.opacity || 1,
            }}
          />
        );
      case 'video':
        if (element.content.includes('youtube') || element.content.includes('youtu.be')) {
          // Handle YouTube videos
          const youtubeId = getYoutubeId(element.content);
          return (
            <iframe
              src={`https://www.youtube.com/embed/${youtubeId}`}
              title="YouTube video"
              allowFullScreen
              style={{
                width: '100%',
                height: '100%',
                border: 'none'
              }}
            />
          );
        } else {
          // Handle direct video files
          return (
            <video 
              src={element.content}
              controls={!isPreviewMode}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'contain'
              }}
            />
          );
        }
      default:
        return <div>Unknown element type</div>;
    }
  };

  const getYoutubeId = (url) => {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  };

  const elementStyle = {
    position: 'absolute',
    left: `${element.position.x}px`,
    top: `${element.position.y}px`,
    transform: `rotate(${currentRotation}deg)`,
    cursor: isPreviewMode ? 'default' : isDragging ? 'grabbing' : 'grab',
    transition: isDragging || isResizing || isRotating ? 'none' : 'all 0.1s ease',
    zIndex: isSelected ? 10 : 1,
    maxWidth: '100%',
    pointerEvents: 'auto'
  };

  return (
    <div 
      ref={elementRef}
      className={`element ${isSelected && !isPreviewMode ? 'selected' : ''}`}
      style={elementStyle}
      onMouseDown={handleMouseDown}
      tabIndex={isPreviewMode ? -1 : 0}
    >
      {getElementContent()}

      {isSelected && !isPreviewMode && (
        <>
          <div className="resize-handle"></div>
          <div className="rotation-handle">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 5C8.13 5 5 8.13 5 12H2L5.84 15.84L9.68 12H7C7 9.24 9.24 7 12 7C14.76 7 17 9.24 17 12C17 14.76 14.76 17 12 17C10.43 17 9.03 16.17 8.3 14.97L6.7 16.57C7.84 18.13 9.78 19 12 19C15.87 19 19 15.87 19 12C19 8.13 15.87 5 12 5Z" fill="white"/>
            </svg>
          </div>

          <div className="element-controls">
            <button className="control-btn duplicate" onClick={handleCopyElement} title="Duplicate">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M16 1H4C2.9 1 2 1.9 2 3V17H4V3H16V1ZM19 5H8C6.9 5 6 5.9 6 7V21C6 22.1 6.9 23 8 23H19C20.1 23 21 22.1 21 21V7C21 5.9 20.1 5 19 5ZM19 21H8V7H19V21Z" fill="white"/>
              </svg>
            </button>
            <button className="control-btn delete" onClick={handleDelete} title="Delete">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M6 19C6 20.1 6.9 21 8 21H16C17.1 21 18 20.1 18 19V7H6V19ZM19 4H15.5L14.5 3H9.5L8.5 4H5V6H19V4Z" fill="white"/>
              </svg>
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default Element;
