import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import Canvas from './components/Canvas';
import ElementProperties from './components/ElementProperties';
import './App.css';

function App() {
  const [elements, setElements] = useState([]);
  const [selectedElement, setSelectedElement] = useState(null);
  const [isPreviewMode, setIsPreviewMode] = useState(false);

  const addElement = (elementType) => {
    const newElement = {
      id: `element-${Date.now()}`,
      type: elementType,
      content: getDefaultContent(elementType),
      style: getDefaultStyle(elementType),
      position: { x: 50, y: 50 }
    };

    setElements([...elements, newElement]);
    setSelectedElement(newElement.id);
  };

  const updateElement = (id, updates) => {
    setElements(elements.map(el => 
      el.id === id ? { ...el, ...updates } : el
    ));
  };

  const deleteElement = (id) => {
    setElements(elements.filter(el => el.id !== id));
    if (selectedElement === id) setSelectedElement(null);
  };

  const togglePreviewMode = () => {
    setIsPreviewMode(!isPreviewMode);
    if (!isPreviewMode) {
      // Entering preview mode, deselect any element
      setSelectedElement(null);
    }
  };

  const addCustomElement = (elementData) => {
    setElements([...elements, elementData]);
    setSelectedElement(elementData.id);
  };

  return (
    <div className="app">
      <Sidebar 
        addElement={addElement} 
        selectedElement={selectedElement}
        element={selectedElement ? elements.find(el => el.id === selectedElement) : null}
        updateElement={updateElement}
      />
      <div className="main-content">
        <div className="toolbar">
          <button onClick={togglePreviewMode}>
            {isPreviewMode ? 'Edit Mode' : 'Preview Mode'}
          </button>
        </div>
        <Canvas 
          elements={elements}
          selectedElement={isPreviewMode ? null : selectedElement}
          setSelectedElement={isPreviewMode ? () => {} : setSelectedElement}
          updateElement={updateElement}
          deleteElement={deleteElement}
          isPreviewMode={isPreviewMode}
          addElement={addCustomElement}
          getDefaultStyle={getDefaultStyle}
        />

        {/* Render ElementProperties as a floating panel when something is selected */}
        {selectedElement && !isPreviewMode && (
          <div className="properties-panel">
            <ElementProperties 
              element={elements.find(el => el.id === selectedElement)} 
              updateElement={updateElement}
            />
          </div>
        )}
      </div>
    </div>
  );
}

// Helper functions
function getDefaultContent(type) {
  switch(type) {
    case 'text': return 'Edit this text';
    case 'heading': return 'Heading';
    case 'button': return 'Click Me';
    default: return '';
  }
}

function getDefaultStyle(type) {
  // Default styling based on element type
  const baseStyle = { 
    width: '300px',
    height: 'auto'
  };

  switch(type) {
    case 'text': 
      return { 
        ...baseStyle,
        fontSize: '16px',
        color: '#333',
        fontFamily: 'Arial',
        lineHeight: '1.5',
        letterSpacing: '0px',
        textAlign: 'left'
      };
    case 'image':
      return {
        ...baseStyle,
        objectFit: 'contain'
      };
    case 'video':
      return {
        ...baseStyle,
        width: '320px',
        height: '240px'
      };
    default: return baseStyle;
  }
}

export default App;
