import React, { useState, useEffect } from 'react';
import './App.css';
import Sidebar from './components/Sidebar';

// Access the server data from the global EDITOR_DATA object
const serverData = window.EDITOR_DATA || {};

console.log("Editor engine initialized with data:", serverData);

function App() {
  const [templateData, setTemplateData] = useState(serverData.templateData || {
    version: "1.0",
    background: {
      type: "color",
      color: "#ffffff"
    },
    elements: []
  });

  const [selectedElement, setSelectedElement] = useState(null);
  const [isPreviewMode, setIsPreviewMode] = useState(false);

  // Get parameters from URL
  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const templateId = queryParams.get('template_id');
    const eventId = queryParams.get('event_id');
    const invitationId = queryParams.get('invitation_id');

    if (templateId) {
      const apiUrl = `${process.env.REACT_APP_API_URL || ''}/editor/api/templates/${templateId}`;
      const fullUrl = `${apiUrl}?${eventId ? 'event_id='+eventId : ''}${invitationId ? '&invitation_id='+invitationId : ''}`;

      console.log("Fetching template data from:", fullUrl);

      fetch(fullUrl)
        .then(response => {
          if (!response.ok) {
            throw new Error(`Network response was not ok: ${response.status}`);
          }
          return response.json();
        })
        .then(data => {
          if (data.success) {
            setTemplateData(data.template_data);
            // Store API endpoints
            window.EDITOR_DATA = {
              templateId: templateId,
              templateName: data.template_name,
              apiEndpoints: {
                save: `${process.env.REACT_APP_API_URL || ''}/editor/engine/${templateId}/save${eventId ? '?event_id='+eventId : ''}${invitationId ? '&invitation_id='+invitationId : ''}`,
                uploadImage: `${process.env.REACT_APP_API_URL || ''}/editor/upload-image`,
                backUrl: `${process.env.REACT_APP_API_URL || ''}/templates`
              }
            };
          } else {
            console.error("Error loading template:", data.error);
          }
        })
        .catch(error => {
          console.error("Failed to load template data:", error);
        });
    }
  }, []);

  // Function to add a new element to the template
  const addElement = (type) => {
    // Example implementation - this would be more complex in the real app
    const newElement = {
      id: Date.now().toString(),
      type,
      content: type === 'text' ? 'New Text' : '',
      position: { x: 50, y: 50 },
      style: {
        fontFamily: 'Arial',
        fontSize: 16,
        color: '#000000'
      }
    };

    setTemplateData(prev => ({
      ...prev,
      elements: [...prev.elements, newElement]
    }));

    setSelectedElement(newElement);
  };

  // Function to save the template data to the server
  const saveTemplate = async () => {
    try {
      const response = await fetch(serverData.apiEndpoints.save, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(templateData),
      });

      const data = await response.json();

      if (data.success) {
        alert('Template saved successfully!');

        // Redirect if there's a back URL
        if (serverData.apiEndpoints.backUrl) {
          window.location.href = serverData.apiEndpoints.backUrl;
        }
      } else {
        alert(`Error saving template: ${data.error}`);
      }
    } catch (error) {
      console.error('Error saving template:', error);
      alert('An error occurred while saving the template');
    }
  };

  // Basic navigation handler
  const handleNavigation = () => {
    if (serverData.apiEndpoints.backUrl) {
      // eslint-disable-next-line no-restricted-globals
      if (confirm('Are you sure you want to leave? Unsaved changes will be lost.')) {
        window.location.href = serverData.apiEndpoints.backUrl;
      }
    }
  };

  console.log("Rendering App with templateData:", templateData);

  return (
    <div className="App">
      <header className="App-header">
        <button className="back-button" onClick={handleNavigation}>
          ‚Üê Back
        </button>
        <h1>{serverData.templateName || 'Template Editor'}</h1>
        <div className="header-actions">
          <button 
            className="preview-button"
            onClick={() => setIsPreviewMode(!isPreviewMode)}
          >
            {isPreviewMode ? 'Edit' : 'Preview'}
          </button>
          <button className="save-button" onClick={saveTemplate}>
            Save
          </button>
        </div>
      </header>

      <div className="editor-container">
        <div className="editor-canvas">
          {/* This would be your canvas editing area */}
          <div className="placeholder-canvas">
            {isPreviewMode ? (
              <div className="preview-mode">
                <h2>Preview Mode</h2>
                <p>This would show the rendered template</p>
              </div>
            ) : (
              <div className="edit-mode">
                <h2>Editor Canvas</h2>
                <p>Elements: {templateData.elements.length}</p>
              </div>
            )}
          </div>
        </div>

        <Sidebar addElement={addElement} isPreviewMode={isPreviewMode} />
      </div>
    </div>
  );
}

export default App;
