import React, { useState } from 'react';
import './Element.css';

function Element({ element, isSelected, onSelect, onDrag, onDelete }) {
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  const handleMouseDown = (e) => {
    onSelect();
    setIsDragging(true);
    const rect = e.target.getBoundingClientRect();
    setDragOffset({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      onDrag(element.id, {
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  React.useEffect(() => {
    if (isDragging) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  const renderElementContent = () => {
    switch(element.type) {
      case 'text':
        return <p style={element.style}>{element.content}</p>;
      case 'heading':
        return <h2 style={element.style}>{element.content}</h2>;
      case 'image':
        return <img src={element.content} alt="User added" style={element.style} />;
      case 'button':
        return <button style={element.style}>{element.content}</button>;
      // Add cases for other element types
      default:
        return null;
    }
  };

  return (
    <div
      className={`element ${isSelected ? 'selected' : ''}`}
      style={{
        position: 'absolute',
        left: `${element.position.x}px`,
        top: `${element.position.y}px`,
        cursor: isDragging ? 'grabbing' : 'grab'
      }}
      onMouseDown={handleMouseDown}
    >
      {renderElementContent()}
      {isSelected && (
        <div className="element-controls">
          <button onClick={() => onDelete(element.id)}>Delete</button>
        </div>
      )}
    </div>
  );
}

export default Element;
