import React, { useState, useRef } from 'react';
import './Element.css';

function Element({ element, isSelected, onSelect, onDrag, onDelete, updateElement, isPreviewMode }) {
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [isRotating, setIsRotating] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [resizeDirection, setResizeDirection] = useState('');
  const [rotationStartAngle, setRotationStartAngle] = useState(0);
  const elementRef = useRef(null);

  // Get the current rotation or default to 0
  const currentRotation = element.style.transform 
    ? parseInt(element.style.transform.replace('rotate(', '').replace('deg)', '')) 
    : 0;

  // Add this function to handle style changes
  const handleStyleChange = (property, value) => {
    updateElement(element.id, { 
      style: { ...element.style, [property]: value } 
    });
  };

  const handleMouseDown = (e) => {
    if (isPreviewMode) return;

    onSelect();
    setIsDragging(true);
    const rect = e.target.getBoundingClientRect();
    setDragOffset({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
  };

  const handleResizeStart = (e, direction) => {
    if (isPreviewMode) return;

    e.stopPropagation();
    setIsResizing(true);
    setResizeDirection(direction);
  };

  const handleRotateStart = (e) => {
    if (isPreviewMode) return;

    e.stopPropagation();
    setIsRotating(true);

    const rect = elementRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    setRotationStartAngle(Math.atan2(e.clientY - centerY, e.clientX - centerX) * (180 / Math.PI));
  };

  const handleMouseMove = (e) => {
    if (isDragging && !isResizing && !isRotating && !isPreviewMode) {
      onDrag(element.id, {
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      });
    } else if (isResizing && !isPreviewMode) {
      const rect = elementRef.current.getBoundingClientRect();

      let newWidth = element.style.width;
      let newHeight = element.style.height;

      if (resizeDirection.includes('e')) {
        newWidth = `${e.clientX - rect.left}px`;
      }
      if (resizeDirection.includes('w')) {
        const diff = rect.left - e.clientX;
        newWidth = `${rect.width + diff}px`;
        onDrag(element.id, { x: element.position.x - diff, y: element.position.y });
      }
      if (resizeDirection.includes('s')) {
        newHeight = `${e.clientY - rect.top}px`;
      }
      if (resizeDirection.includes('n')) {
        const diff = rect.top - e.clientY;
        newHeight = `${rect.height + diff}px`;
        onDrag(element.id, { x: element.position.x, y: element.position.y - diff });
      }

      updateElement(element.id, { 
        style: { 
          ...element.style, 
          width: newWidth, 
          height: newHeight
        } 
      });
    } else if (isRotating && !isPreviewMode) {
      const rect = elementRef.current.getBoundingClientRect();
      const centerX = rect.left + rect.width / 2;
      const centerY = rect.top + rect.height / 2;

      const angle = Math.atan2(e.clientY - centerY, e.clientX - centerX) * (180 / Math.PI);
      const rotation = currentRotation + (angle - rotationStartAngle);

      updateElement(element.id, {
        style: {
          ...element.style,
          transform: `rotate(${rotation}deg)`
        }
      });

      setRotationStartAngle(angle);
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    setIsResizing(false);
    setIsRotating(false);
  };

  React.useEffect(() => {
    if (isDragging || isResizing || isRotating) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging, isResizing, isRotating]);

  const renderElementContent = () => {
    switch(element.type) {
      case 'text':
        return <p style={element.style} contentEditable={!isPreviewMode} suppressContentEditableWarning>{element.content}</p>;
      case 'image':
        return <img src={element.content || 'https://via.placeholder.com/200x150'} alt="User added" style={element.style} />;
      case 'video':
        // Simple video embed handling
        const videoUrl = element.content || '';
        if (videoUrl.includes('youtube.com') || videoUrl.includes('youtu.be')) {
          return (
            <iframe 
              src={videoUrl.replace('watch?v=', 'embed/')} 
              style={element.style}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
              allowFullScreen
              title="Embedded video"
            />
          );
        } else {
          return (
            <video 
              src={videoUrl} 
              controls 
              style={element.style}
            >
              Your browser does not support the video tag.
            </video>
          );
        }
      default:
        return null;
    }
  };

  return (
    <div
      ref={elementRef}
      className={`element ${isSelected ? 'selected' : ''} ${isPreviewMode ? 'preview-mode' : ''}`}
      style={{
        position: 'absolute',
        left: `${element.position.x}px`,
        top: `${element.position.y}px`,
        width: element.style.width,
        height: element.style.height,
        transform: element.style.transform || 'rotate(0deg)',
        cursor: isPreviewMode ? 'default' : (isDragging ? 'grabbing' : 'grab')
      }}
      onMouseDown={handleMouseDown}
    >
      {renderElementContent()}
      {isSelected && !isPreviewMode && (
        <>
          <div className="element-controls">
            <button onClick={() => onDelete(element.id)}>Delete</button>
          </div>
          {/* Resize handles */}
          <div className="resize-handle n" onMouseDown={(e) => handleResizeStart(e, 'n')}></div>
          <div className="resize-handle e" onMouseDown={(e) => handleResizeStart(e, 'e')}></div>
          <div className="resize-handle s" onMouseDown={(e) => handleResizeStart(e, 's')}></div>
          <div className="resize-handle w" onMouseDown={(e) => handleResizeStart(e, 'w')}></div>
          <div className="resize-handle ne" onMouseDown={(e) => handleResizeStart(e, 'ne')}></div>
          <div className="resize-handle se" onMouseDown={(e) => handleResizeStart(e, 'se')}></div>
          <div className="resize-handle sw" onMouseDown={(e) => handleResizeStart(e, 'sw')}></div>
          <div className="resize-handle nw" onMouseDown={(e) => handleResizeStart(e, 'nw')}></div>

          {/* Rotation handle */}
          <div className="rotation-handle" onMouseDown={handleRotateStart}>⟲</div>
        </>
      )}

      {isSelected && element.type === 'text' && !isPreviewMode && (
        <div className="floating-toolbar">
          <button className="toolbar-btn" onClick={() => handleStyleChange('fontWeight', element.style.fontWeight === 'bold' ? 'normal' : 'bold')}>
            <span className={element.style.fontWeight === 'bold' ? 'active' : ''}>B</span>
          </button>
          <button className="toolbar-btn" onClick={() => handleStyleChange('fontStyle', element.style.fontStyle === 'italic' ? 'normal' : 'italic')}>
            <span className={element.style.fontStyle === 'italic' ? 'active' : ''}>I</span>
          </button>
          <div className="text-type-selector">
            <select onChange={(e) => handleStyleChange('fontSize', e.target.value)}>
              <option value="12px">Small</option>
              <option value="16px">Normal</option>
              <option value="24px">Heading 3</option>
              <option value="32px">Heading 2</option>
              <option value="40px">Heading 1</option>
            </select>
          </div>
        </div>
      )}
    </div>
  );
}

export default Element;
