import React, { useCallback, useRef, useEffect, useState } from 'react';
import Element from './Element';
import './Canvas.css';

function Canvas({ elements, selectedElement, setSelectedElement, updateElement, deleteElement, isPreviewMode, addElement, getDefaultStyle }) {
  const canvasRef = useRef(null);
  const [canvasBounds, setCanvasBounds] = useState({ left: 0, right: 0, top: null, bottom: null });

  // Calculate and update canvas boundaries when component mounts or window resizes
  useEffect(() => {
    const updateCanvasBounds = () => {
      if (canvasRef.current) {
        const rect = canvasRef.current.getBoundingClientRect();

        // Get the width of the properties panel more accurately
        const propertiesPanel = document.querySelector('.element-properties');
        const sidebarWidth = propertiesPanel ? propertiesPanel.getBoundingClientRect().width : 300;

        // Calculate the effective right boundary by subtracting sidebar width
        const effectiveRight = window.innerWidth - sidebarWidth;

        // Set bounds with only horizontal restrictions - no vertical restrictions
        setCanvasBounds({
          left: rect.left + 16,
          right: effectiveRight - 16,
          // Remove top/bottom constraints to allow infinite vertical scrolling
          top: null,
          bottom: null
        });
      }
    };

    updateCanvasBounds();
    window.addEventListener('resize', updateCanvasBounds);

    const observer = new MutationObserver(updateCanvasBounds);
    observer.observe(document.body, { 
      childList: true, 
      subtree: true,
      attributes: true,
      attributeFilter: ['style', 'class']
    });

    return () => {
      window.removeEventListener('resize', updateCanvasBounds);
      observer.disconnect();
    };
  }, []);

  const handleCanvasClick = (e) => {
    // Deselect when clicking on the canvas background
    if (e.target === e.currentTarget && !isPreviewMode) {
      setSelectedElement(null);
    }
  };

  // This function enforces element boundaries
  const enforceElementBoundaries = (id, newPosition, elementWidth, elementHeight) => {
    // Calculate max bounds to keep element within canvas
    const maxX = canvasBounds.right - canvasBounds.left - elementWidth;
    const maxY = canvasBounds.bottom - canvasBounds.top - elementHeight;

    // Apply constraints
    return {
      x: Math.max(0, Math.min(newPosition.x, maxX)),
      y: Math.max(0, Math.min(newPosition.y, maxY))
    };
  };

  const handleElementDrag = (id, position, elementWidth, elementHeight) => {
    if (!isPreviewMode) {
      // Get constrained position
      const constrainedPosition = enforceElementBoundaries(id, position, elementWidth, elementHeight);
      updateElement(id, { position: constrainedPosition });
    }
  };

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    if (isPreviewMode) return;

    // Get drop position
    const rect = e.currentTarget.getBoundingClientRect();
    const position = {
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    };

    // Check if files were dropped
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const file = e.dataTransfer.files[0];

      // Check if it's an image
      if (file.type.startsWith('image/')) {
        const reader = new FileReader();
        reader.onload = (event) => {
          // Create a new image element
          const newElement = {
            id: `element-${Date.now()}`,
            type: 'image',
            content: event.target.result,
            style: getDefaultStyle('image'),
            position: position
          };
          addElement(newElement);
        };
        reader.readAsDataURL(file);
      }
    }
  }, [isPreviewMode, addElement, getDefaultStyle]);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
  }, []);

  return (
    <div 
      ref={canvasRef}
      className={`canvas ${isPreviewMode ? 'preview-mode' : ''}`} 
      onClick={handleCanvasClick}
      onDrop={handleDrop}
      onDragOver={handleDragOver}
    >
      {elements.map(element => (
        <Element
          key={element.id}
          element={element}
          isSelected={element.id === selectedElement}
          onSelect={setSelectedElement}
          onUpdate={(id, updates, isNew = false) => {
            if (isNew) {
              // This is for adding a duplicated element
              addElement(updates);
            } else {
              // Normal update
              updateElement(id, updates);
            }
          }}
          onDelete={deleteElement}
          isPreviewMode={isPreviewMode}
          canvasBounds={canvasBounds}
        />
      ))}
    </div>
  );
}

export default Canvas;
