import React, { useState, useEffect } from 'react';
import Canvas from './components/Canvas';
import Sidebar from './components/Sidebar';
import ElementProperties from './components/ElementProperties';
import './App.css';

function App() {
  const [elements, setElements] = useState([]);
  const [selectedElement, setSelectedElement] = useState(null);
  const [isPreviewMode, setIsPreviewMode] = useState(false);

  // Find the selected element based on its ID
  const selectedElementData = elements.find(el => el.id === selectedElement);

  // Add a new element to the canvas
  const addElement = (element) => {
    setElements(prevElements => [...prevElements, element]);
    setSelectedElement(element.id);
  };

  // Update an existing element's properties
  const updateElement = (id, updates, isDuplicate = false) => {
    if (isDuplicate) {
      // For duplicates, add a new element instead
      addElement(updates);
      return;
    }

    setElements(prevElements => 
      prevElements.map(el => el.id === id ? { ...el, ...updates } : el)
    );
  };

  // Delete an element from the canvas
  const deleteElement = (id) => {
    setElements(prevElements => prevElements.filter(el => el.id !== id));
    if (selectedElement === id) {
      setSelectedElement(null);
    }
  };

  // Get default styling based on element type
  const getDefaultStyle = (type) => {
    switch(type) {
      case 'text':
        return {
          color: '#000000',
          fontSize: '16px',
          fontFamily: 'Arial, sans-serif',
          textAlign: 'left',
          width: '200px',
          height: 'auto'
        };
      case 'image':
        return {
          width: '250px',
          height: '250px',
          objectFit: 'contain'
        };
      case 'video':
        return {
          width: '320px',
          height: '240px'
        };
      default:
        return {};
    }
  };

  return (
    <div className="app">
      <div className="app-header">
        <h1>Page Designer</h1>
        <button 
          className="preview-toggle" 
          onClick={() => setIsPreviewMode(!isPreviewMode)}
        >
          {isPreviewMode ? 'Edit Mode' : 'Preview Mode'}
        </button>
      </div>

      <div className="app-content">
        <div className="sidebar-container">
          <Sidebar 
            addElement={addElement} 
            getDefaultStyle={getDefaultStyle}
            isPreviewMode={isPreviewMode}
          />
        </div>

        <div className="canvas-container">
          <div className="canvas-wrapper">
            <Canvas 
              elements={elements}
              selectedElement={selectedElement}
              setSelectedElement={setSelectedElement}
              updateElement={updateElement}
              deleteElement={deleteElement}
              isPreviewMode={isPreviewMode}
              addElement={addElement}
              getDefaultStyle={getDefaultStyle}
            />
          </div>
        </div>

        <div className="properties-container">
          {selectedElementData && !isPreviewMode && (
            <ElementProperties 
              element={selectedElementData} 
              updateElement={updateElement}
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
