import React from 'react';
import Element from './Element';
import './Canvas.css';

function Canvas({ elements, selectedElement, setSelectedElement, updateElement, deleteElement, isPreviewMode }) {
  const handleCanvasClick = (e) => {
    // Deselect when clicking on the canvas background
    if (e.target === e.currentTarget && !isPreviewMode) {
      setSelectedElement(null);
    }
  };

  const handleElementDrag = (id, position) => {
    if (!isPreviewMode) {
      updateElement(id, { position });
    }
  };

  return (
    <div className={`canvas ${isPreviewMode ? 'preview-mode' : ''}`} onClick={handleCanvasClick}>
      {elements.map(element => (
        <Element
          key={element.id}
          element={element}
          isSelected={selectedElement === element.id}
          onSelect={() => !isPreviewMode && setSelectedElement(element.id)}
          onDrag={handleElementDrag}
          onDelete={deleteElement}
          isPreviewMode={isPreviewMode}
        />
      ))}
    </div>
  );
}

export default Canvas;
